 #include<iostream>using std::cin;using std::cout;#define endl '\n'int dp[2][220][220];char s[1010],m[220];const int mod = 1000000007;int main(){	int ls,lm,kk;	cin>>ls>>lm>>kk;	cin>>(s+1)>>(m+1);	dp[1][0][0]=1;	for(int i = 1;i <= ls;++i){		for(int j = lm;j;--j){			for(int k = kk;k;--k){				if(s[i]==m[j])dp[0][j][k]=(dp[0][j-1][k]+dp[1][j-1][k-1])%mod;				else dp[0][j][k]=0;				dp[1][j][k] = (dp[1][j][k]+dp[0][j][k])%mod;			}		}	}	cout<<dp[1][lm][kk]<<endl;}